$(document).ready(function(){$(".nav-tabs > li a[title]").tooltip();$('a[data-toggle="tab"]').on("show.bs.tab",function(b){var a=$(b.target);if(a.parent().hasClass("disabled")){return false}});$(".next-step").click(function(b){var a=$(".wizard .nav-tabs li.active");a.next().removeClass("disabled");nextTab(a)});$(".prev-step").click(function(b){var a=$(".wizard .nav-tabs li.active");prevTab(a)})});function nextTab(a){$(a).next().find('a[data-toggle="tab"]').click()}function prevTab(a){$(a).prev().find('a[data-toggle="tab"]').click()};